export const getOrderITYouthAdminAPI = () => {
  /**
   * @summary Kh·ªüi t·∫°o checkout c√¥ng khai
   */
  const ordersControllerCheckout = <TData = AxiosResponse<OrderResponseDto>>(
    checkoutOrderDto: CheckoutOrderDto,
    options?: AxiosRequestConfig,
  ): Promise<TData> => {
    return axios.post(`/api/orders/checkout`, checkoutOrderDto, options);
  };

  /**
   * @summary L·∫•y th√¥ng tin thanh to√°n VietQR
   */
  const ordersControllerGetPaymentIntent = <
    TData = AxiosResponse<PaymentIntentResponseDto>,
  >(
    code: string,
    options?: AxiosRequestConfig,
  ): Promise<TData> => {
    return axios.get(`/api/orders/${code}/payment-intent`, options);
  };

  return { ordersControllerCheckout, ordersControllerGetPaymentIntent };
};
export type OrdersControllerCheckoutResult = AxiosResponse<OrderResponseDto>;
export type OrdersControllerGetPaymentIntentResult =
  AxiosResponse<PaymentIntentResponseDto>;
